mapper.select:
select

,

from goods


    and goods_id = #{param1.goodsId}


    and goods_type_id = #{param1.goodsTypeId}


    and goods_name like concat('%',#{param1.goodsName},'%')


    and goods_price = #{param1.goodsPrice}


    and gmt_modified >= #{beginTime}


    and gmt_modified <= #{endTime}

   and is_deleted = #{param1.isDeleted}
